mapper.select:
SELECT product.product_id as "productId", product.product_name as "productName", product.product_price as "productPrice", product.product_description as "productDescription", product_image.image_id as "imageId", product_image.image_name as "imageName", product_image.image_size as "imageSize", product_image.image_type as "imageType", product_image.image_data as "imageData", cart.cart_cnt as "cartCnt" FROM cart JOIN product ON cart.product_id=product.product_id JOIN product_image ON product.product_id=product_image.product_id WHERE cart_id IN
#{id}
AND user_id=#{userId}
